mapper.select:
select m.*,if(om.id is null,0,1) as checked from b_menus m left join b_organization_menu om on om.menu_id = m.id and om.organization_id=#{ organizationId }
m.status = 1
order by m.sort

select * from b_menus m
right join b_organization_menu om on om.menu_id = m.id and om.organization_id =#{ organizationId }
where m.`status` = 1
